import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import {useDispatch, useSelector} from "react-redux";
import {useEffect, useState} from "react"

import {uiActions} from "./store/ui-slice";

function App() {
    const isCartVisible = useSelector((state) => state.ui.isVisible);
    const cart = useSelector(state => state.cart);
    const notification = useState(state => state.ui.notification);

    const dispatch = useDispatch();
    useEffect(() => {
        const sendData = async () => {
            dispatch(uiActions.showNotification({
                status: 'pending', title: 'Sending...', message: 'Sending data...',
            }));
            const res = await fetch(`https://react-practice-10998-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json`, {
                method: 'PUT', body: JSON.stringify({cart}),

            })

            if (!res.ok) {
                throw new Error("Failed to fetch cart data.");
            }

            dispatch(uiActions.showNotification({
                status: 'success', title: 'Success!', message: 'Sending data successfully...',
            }));

            const data = await res.json();

            sendData().catch((err) => {
                dispatch(uiActions.showNotification({
                    status: 'error', title: 'Error!', message: 'Sending data failed...',
                }));
            })
        }
    }, [cart, dispatch]);

    return (<Layout>
        {isCartVisible && <Cart/>}
        <Products/>
    </Layout>);
}

export default App;
