import {createStore} from 'redux';
import {createSlice} from "@reduxjs/toolkit";

const states = {counter: 0, showCounter: true};

createSlice({
    name: "counter",
    initialState: states,
    reducers: {
        increment: (state) => {
            state.counter++;
        },
        decrement: (state) => {
            state.counter--;
        },
        increase: (state, action) => {
            state.counter + action.payload;
        },
        toggleCounter: (state, action) => {},
    },
});

const counterReducer = (state = states, action) => {
    if (action.type === 'INCREMENT') {
        return {counter: state.counter + 1, showCounter: state.showCounter};
    }
    if (action.type === 'INCREASE') {
        return {counter: state.counter + action.amount, showCounter: state.showCounte};
    }
    if (action.type === 'DECREMENT') {
        return {counter: state.counter - 1, showCounter: state.showCounte};
    }
    if (action.type === 'TOGGLE_COUNTER') {
        return {
            showCounter: !state.showCounter, counter: state.counter
        }
    }
    return state;
};

const store = createStore(counterReducer);

export default store;
